error.pattern:
// BinaryExpr [ Op::Op [ @Code = KTC_OPCODE_ADDASSIGN |
                         @Code = KTC_OPCODE_SUBASSIGN ] ]
              [ Left.getTypeName() = 'BSTR' ]

// CallExpr [ Func::IdExpr [ @Id = 'SysFreeString' ] ]
            [ Args[0]::* [ $name := getTypeName() ]
                         [ $name != 'BSTR' ] ]

// BinaryExpr [ Op::Op [ @Code = KTC_OPCODE_ASSIGN ] ]
              [ Right.isVariable() ]
